$.fn.rotator=function(){"use strict";return this.each(function(){const t=$(this),o=$(this).find(".rotator__slide"),r=o.length;if(r>1){o.first().find("picture").clone().appendTo(t),t.addClass("js-rotator--active");let a,e=$('<ul class="rotator__bullets"></ul>');for(e.appendTo(t),a=1;a<=r;a++)e.append("<li><button>Slide "+a+"</button></li>");const s=e.find("button");s.first().addClass("rotator__bullet--active"),s.on("click",function(){var t=$(this).parent().index();o.removeClass("rotator__slide--current").eq(t).addClass("rotator__slide--current"),s.removeClass("rotator__bullet--active"),$(this).addClass("rotator__bullet--active")})}})},$(function(){$(".js-rotator").rotator()});